(820, '2012-01-18 07:44:09', 1, '/public/uploads/day_120118/201201180044093994.jpg', 'http://usa.frenchconnection.com/category.aspx?categoryid=woman&seoterm=woman&siteID=piaqBdsdpmQ-_preihQBSMRn1B1yhOhRQQ&mscsmigrated=true', 'French Connection: extra 60% off sale items + free shipping any order', '<p> French Connection is offering <strong>an extra 60% off</strong> any sale items plus free shipping on orders. Enter code <strong>FCNOW</strong> at checkout.</p>', 18, 'Huaren Fashion'),
(821, '2012-01-18 07:44:10', 1, '/public/uploads/day_120118/201201180044109968.jpg', 'http://www.jcrew.com/sale.jsp', 'J.Crew – Extra 30% off Final Sale', '<p class="body"> JCrew is offering an extra 30% off on its Final Sale items via code “<strong>MUSTHAVE</strong>“.&nbsp; Shipping is about $9. Note that there are no returns or exchanges for Final Sale items. Offer valid through 1/20.</p>', 17, 'Huaren Fashion'),
